#ifndef LCTCOLOR_H_INCLUDED
#define LCTCOLOR_H_INCLUDED
/****************************************************************************/
/* Colors */
//                          xxBBGGRR
#define  BLACK            0x00000000
#define  GRAY             0x00565656
#define  WHITE            0x00aaaaaa
#define  BRIGHT_WHITE     0x00FFFFFF

#define  BLUE             0x00aa0000
#define  LIGHT_BLUE       0x00FF5656

#define  GREEN            0x0000aa00
#define  LIGHT_GREEN      0x0056ff56

#define  RED              0x000000aa
#define  LIGHT_RED        0x005656FF

#define  CYAN             0x00aaaa00 // blue and green
#define  LIGHT_CYAN       0x00ffFF56

#define  MAGENTA          0x00aa00aa // blue and red
#define  LIGHT_MAGENTA    0x00ff56ff

#define  BROWN            0x000056aa // red with some green
#define  YELLOW           0x0056ffff // bright white less some blue

/***************************************************************************/
#endif // LCTCOLOR_H_INCLUDED
/***************************************************************************/
